import { useState } from 'react';


function Square({value,  OnSquareClick}) {

  return (<button className="square" onClick={OnSquareClick}>{value}</button>);
}


export default function Board() {

  let mv;
  let cent;

  const [selectedSquare, setSelectedSquare] = useState(null);
  const [moves, setmoves] = useState(0);

  const [nextX, setnextX] = useState(true);

  const [squares, setSquares] = useState(Array(9).fill(null));

  function handleClick(i){
    setmoves(moves+1);
    if (moves < 6){
      if (squares[i] || calculateWinner(squares)){
        return;
      }
      const nextSquares = squares.slice();
      if (nextX) {
        nextSquares[i] = "X";
      } else{
        nextSquares[i] = "O";
      }
      setSquares(nextSquares);
      setnextX(!nextX);
    }
    else if (moves >= 6){
      if (selectedSquare == null){
        setSelectedSquare(i);
      }
      else{
        const newSquares = squares.slice();
        if (!newSquares[i] && (squares[selectedSquare] === (nextX ? 'X' : 'O')) && canMove(selectedSquare, i)){
          newSquares[i] = nextX ? 'X' : 'O'
          newSquares[selectedSquare] = null;
          setSquares(newSquares);
          setnextX(!nextX);
        }
        setSelectedSquare(null);
      }
    }

  } 


  if(moves >= 6){
    mv = "three by each!";
    if(squares[4] && canWin(squares)){
      cent = "Center piece selected, move piece to win!";
    }
    else if(squares[4]){
      cent = "Center piece selected, cannot win, so vacate!";
    }
  }


  const winner = calculateWinner(squares);
  let status;
  if (winner){
    status = 'Winner: ' + winner;
  } else {
    status = 'Next player: ' + (nextX ? 'X' : 'O');
  }

  return (
    <>
      <div className="status">{status}</div>
      <div className="moves">{mv}</div>
      <div className="centerpiece">{cent}</div>
      <div className="board-row">
        <Square value={squares[0]} OnSquareClick={() => handleClick(0)}/>
        <Square value={squares[1]} OnSquareClick={() => handleClick(1)}/>
        <Square value={squares[2]} OnSquareClick={() => handleClick(2)}/>
      </div>
      <div className="board-row">
        <Square value={squares[3]} OnSquareClick={() => handleClick(3)}/>
        <Square value={squares[4]} OnSquareClick={() => handleClick(4)}/>
        <Square value={squares[5]} OnSquareClick={() => handleClick(5)}/>
      </div>

      <div className="board-row">
        <Square value={squares[6]} OnSquareClick={() => handleClick(6)}/>
        <Square value={squares[7]} OnSquareClick={() => handleClick(7)}/>
        <Square value={squares[8]} OnSquareClick={() => handleClick(8)}/>
      </div>

    </>
  );
}


function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

function canMove(src, dest){
  switch(src){
    case 0:
      if(dest == 1 || dest == 3 || dest == 4){return true}
      else {return false};
    case 1:
      if(dest == 0 || dest == 2 || dest == 3 || dest == 4 || dest == 5){return true}
      else {return false};
    case 2:
      if(dest == 1 || dest == 4 || dest == 5){return true}
      else {return false};
    case 3:
      if(dest == 0 || dest == 1 || dest == 4 || dest == 6 || dest == 7){return true}
      else {return false};
    case 4:
      if(dest == 0 || dest == 1 || dest == 2 || dest == 3 || dest == 5 || dest == 6 || dest == 7 || dest == 8){return true}
      else {return false};
    case 5:
      if(dest == 1 || dest == 2 || dest == 4 || dest == 7 || dest == 8){return true}
      else {return false};
    case 6:
      if(dest == 3 || dest == 4 || dest == 7){return true}
      else {return false};
    case 7:
      if(dest == 6 || dest == 3 || dest == 4 || dest == 5 || dest == 8){return true}
      else {return false};
    case 8:
      if(dest == 4 || dest == 7 || dest == 5){return true}
      else {return false};
    default:
      return false;
  }
}

function canWin(squares){
  let count = 0;
  if((((!squares[0] && squares[8]) && (canMove(3,0) || canMove(1,0))) || ((squares[8] && !squares[0]))&&(canMove(5,8) || canMove(7,8))) ){
    count = count + 1;
  }
  else if(((squares[1] && !squares[7]) && (canMove(3,7) || canMove(6,7) || canMove(5,7) || canMove (8,7))) || ((squares[7] && !squares[1]) && (canMove(3,1) || canMove(0,1) || canMove(5,1) || canMove(2,1)))){
    count = count + 1;
  }
  else if(((squares[2] && !squares[6]) && (canMove(3,6) || canMove(7,6))) || ((squares[6] && !squares[2]) && (canMove(1,2) && canMove(5,2)))){
    count = count + 1;
  }
  else if(((squares[3] && !squares[5]) && (canMove(1,5) || canMove(2,5) || canMove(8,5) || canMove(7,5))) || ((squares[5] && !squares[3]) && (canMove(0,3) || canMove(1,3) || canMove(7,3) || canMove(6,3)))){
    count = count + 1;
  }

  if (count > 0){return true;}
  else{return false;}

}
