import { useState, useEffect } from 'react';

function Square({value, onSquareClick}) {
  return (
    <button className="square" onClick={onSquareClick}>
      {value}
    </button>
  );
}

export default function Board() {
  const [xIsNext, setXIsNext] = useState(true);
  const [xmoves, setxmoves] = useState(0);
  const [omoves, setomoves] = useState(0);
  const [xmess, setxmess] = useState(null);
  const [omess, setomess] = useState(null);
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [game, setgame] = useState('standard');
  const [select, setselect] = useState(null);
  const [moves, setmoves] = useState(0);
  const [canwin, setwin] = useState(null);
  const current = xIsNext ? 'X' : 'O';
  const [centerlock, setlock] = useState(false);
  const [locked, setlockmess] = useState(null);

  useEffect(() => {
    if (xmoves >= 3) {
      setxmess("X has made at least 3 moves!");
    }if (omoves >= 3){
      setomess("O has made at least 3 moves!");
      setgame('chorus');
    }
  }, [xmoves, omoves]);

  useEffect(() => {
    if(squares[4] !== current){
      setwin(null);
    }
    if(squares[4] !== null && canWin(squares, current) && squares[4] === current){
      setlock(true);
      setwin('Player ' + current + ', your next move can win. Either do so or vacate the center square.');
    } else if(squares[4] !== null && !canWin(squares,current)){
      setlock(true);
      setwin('Player ' + current + ', you cannot win, please vacate the center square.');
    }else if(!squares[4]){setwin(null);}

  }, [squares, current, centerlock]);

  function handleClick(i){
    if(game === 'standard'){
      standard(i);
    } else if (game === 'chorus'){
      chorus(i);
    }
  }

  function standard(i) {
    if (calculateWinner(squares) || squares[i] || xmoves + omoves >= 6) {
      return;
    }
    const nextSquares = squares.slice();
    if (xIsNext) {
      setxmoves(xmoves +1);
      nextSquares[i] = 'X';
    } else {
      setomoves(omoves +1);

      nextSquares[i] = 'O';
    }
    setSquares(nextSquares);
    setXIsNext(!xIsNext);
  }

  function chorus(i){

    if(select === null && moves === 0){
      setselect(i);
      setmoves(1);
    }
    else if (i!=select && (squares[i] === null) && squares[select] === current && moves ==1 && canMove(select, i)){
      const nextSq = squares.slice();
      if(centerlock === true){
        if (select!==4){
          setmoves(0);
          setlockmess("invalid move. pick the center square");
          return;
        }
        else if(select===4){
          setlock(false);
          setlockmess('fine!');
        }
      }
      if (xIsNext) {
        setxmoves(xmoves +1);
        nextSq[i] = 'X';
        nextSq[select] = null;
      } else {
        nextSq[i] = 'O';
        nextSq[select] = null;
      }
      setSquares(nextSq);
      setXIsNext(!xIsNext);
      setselect(null);
      setmoves(0);
    } else{
      setselect(null);
      setmoves(0);
      return;
    }
  }


  const winner = calculateWinner(squares);
  let status;
  if (winner) {
    status = 'Winner: ' + winner;
  } else {
    status = 'Next player: ' + (xIsNext ? 'X' : 'O');
  }

  return (
    <>
      <div className="status">{status}</div>
      {/* <div className="xm">{xmoves}</div>
      <div className="om">{omoves}</div> */}
      <div className="xmess">{xmess}</div>
      <div className="omess">{omess}</div>
      <div className="canwin">{canwin}</div>
      <div className="locked">{locked}</div>
      <div className="board-row">
        <Square value={squares[0]} onSquareClick={() => handleClick(0)} />
        <Square value={squares[1]} onSquareClick={() => handleClick(1)} />
        <Square value={squares[2]} onSquareClick={() => handleClick(2)} />
      </div>
      <div className="board-row">
        <Square value={squares[3]} onSquareClick={() => handleClick(3)} />
        <Square value={squares[4]} onSquareClick={() => handleClick(4)} />
        <Square value={squares[5]} onSquareClick={() => handleClick(5)} />
      </div>
      <div className="board-row">
        <Square value={squares[6]} onSquareClick={() => handleClick(6)} />
        <Square value={squares[7]} onSquareClick={() => handleClick(7)} />
        <Square value={squares[8]} onSquareClick={() => handleClick(8)} />
      </div>
    </>
  );

}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

function canMove(src, dest){
  const moves = [
    [1,3,4],
    [0,2,3,4,5],
    [1,4,5],
    [0,1,4,6,7],
    [0,1,2,3,5,6,7,8],
    [1,2,4,7,8],
    [3,4,7],
    [6,3,4,5,8],
    [4,7,5]

  ];
  switch(src){
    case 0:
      if(moves[0].includes(dest)){return true}
      else {return false};
    case 1:
      if(moves[1].includes(dest)){return true}
      else {return false};
    case 2:
      if(moves[2].includes(dest)){return true}
      else {return false};
    case 3:
      if(moves[3].includes(dest)){return true}
      else {return false};
    case 4:
      if(moves[4].includes(dest)){return true}
      else {return false};
    case 5:
      if(moves[5].includes(dest)){return true}
      else {return false};
    case 6:
      if(moves[6].includes(dest)){return true}
      else {return false};
    case 7:
      if(moves[7].includes(dest)){return true}
      else {return false};
    case 8:
      if(moves[8].includes(dest)){return true}
      else {return false};
    default:
      return false;
  }
}




